#!/usr/bin/env python


dna = ''

#dna = 'AGGCGTTA'
dnalist = list(dna)
pos = 0

for nt in dnalist:
	if nt == 'A':
		dnalist[pos] = 'T'
	elif nt == 'T':
		dnalist[pos] = 'A'
	elif nt == 'G':
		dnalist[pos] = 'C'
	elif nt == 'C':
		dnalist[pos] = 'G'

	pos += 1
joiner=''
comp = joiner.join(dnalist)
print(dna)
print(comp)
print(comp[::-1])
